contracts/Base.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "./interface/IConfig.sol";

abstract contract Base is ContextUpgradeable, ReentrancyGuardUpgradeable {
    /// @notice The precision of the fee
    uint64 public constant FEE_PRECISION = 1e6;

    /// @notice The default admin role
    bytes32 public constant DEFAULT_ADMIN_ROLE = 0x00;

    /// @notice The role for the tmelock
    bytes32 public constant TIMELOCK_ROLE = keccak256("TIMELOCK_ROLE");

    /// @notice The role for the admin
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    /// @notice The role for the backend
    bytes32 public constant BACKEND_ROLE = keccak256("BACKEND_ROLE");

    /// @notice sveMetis vault initial deposit amount
    uint256 public constant INITIAL_DEPOSIT_AMOUNT = 1 ether;

    /// @notice holds the address of the config contract
    IConfig public config;

    address private internalCalling;


    modifier onlyRole(bytes32 role) {
        if (!config.hasRole(role, _msgSender())) {
          _checkRole(role);
        }  
        _;
    }

    modifier onlyBackend() {
        _checkRole(BACKEND_ROLE);
        _;
    }

    modifier internalOnly(address internalAddress) {
        require(_msgSender() == internalAddress, "internal only");
        internalCalling = internalAddress;
        _;
        internalCalling = address(0);
    }

    /// @notice Initializes the contract with the config contract address
    /// @param _config The address of the config contract
    function __Base_init(address _config) internal onlyInitializing {
        config = IConfig(_config);
        __ReentrancyGuard_init();
        __Context_init();
    }

  /// @dev Revert with a standard message if `_msgSender()` is missing `role`.
    function _checkRole(bytes32 role) internal view virtual {
        _checkRole(role, _msgSender());
    }

    /**
     * @dev Revert with a standard message if `account` is missing `role`.
     *
     * The format of the revert reason is given by the following regular expression:
     *
     *  /^AccessControl: account (0x[0-9a-f]{40}) is missing role (0x[0-9a-f]{64})$/
     */
    function _checkRole(bytes32 role, address account) internal view virtual {
        if (!config.hasRole(role, account)) {
            revert(
                string(
                    abi.encodePacked(
                        "AccessControl: account ",
                        Strings.toHexString(account),
                        " is missing role ",
                        Strings.toHexString(uint256(role), 32)
                    )
                )
            );
        }
    }

    /**
     * reject Metis transfer
     */
    receive() external payable {
        revert("Base: not support Metis transfer");
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[30] private __gap;
}

contracts/interface/IConfig.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

interface IConfig {
    function initialize() external;

    function veMetis() external view returns (address);

    function veMetisMinter() external view returns (address);

    function sveMetis() external view returns (address);

    function rewardDispatcher() external view returns (address);

    function metis() external view returns (address);

    function bridge() external view returns (address);

    function l1Dealer() external view returns (address);

    function protocolTreasury() external view returns (address);

    function protocolTreasuryRatio() external view returns (uint32);

    function redemptionQueue() external view returns (address);

    function queueLengthSecs() external view returns (uint64);

    function setProtocolTreasury(address _protocolTreasury) external;


    function setProtocolTreasuryRatio(uint32 _protocolTreasuryRatio) external;
    function setQueueLengthSecs(uint64 _queueLengthSecs) external;

    function hasRole(
        bytes32 role,
        address account
    ) external view returns (bool);

    function getRoleAdmin(bytes32 role) external view returns (bytes32);

    function grantRole(bytes32 role, address account) external;

    function revokeRole(bytes32 role, address account) external;

    function renounceRole(bytes32 role, address account) external;

    function setRoleAdmin(bytes32 role, bytes32 adminRole) external;
}

